import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { useContext } from 'react'
import { Button, Text, TouchableOpacity, View, Modal, Alert } from 'react-native'
import { TextInput, TouchableWithoutFeedback } from 'react-native-gesture-handler';
import { AuthNavProps } from '../navigation/AuthParamList';
import { AuthContext } from '../navigation/AuthProvider';
import {WindowsModal,BoxAuthentification} from '../CSS/styleA'
export function Login({navigation}: AuthNavProps<'Login'>){
    const {login} = useContext(AuthContext);
    const [TextEmail, setTextEmail] = React.useState('');
    const [TextPassword, setTextPassword] = React.useState('');
    const [RetrievePassword, setRetrievePassword] = React.useState(false);
    return (

    <View>
        {RetrievePassword ?
        <View style={WindowsModal.BigMain}>
            <View>
                <TouchableOpacity
                style={WindowsModal.Background}
                onPress={() => setRetrievePassword(false)}>
                </TouchableOpacity>
            </View>
            <View
                style = {WindowsModal.Main}>
                <Text>Renseignez votre adresse mail ci dessous. 
                    Vous recevrez un email contenant votre nouveau mot de passe que vous pourrez modifier par la suite dans votre profil
                </Text>
                <TextInput
                        autoCompleteType = {'email'}
                        autoCorrect = {false}
                        editable = {true}
                        placeholder = {'Adresse mail'}
                        value = {TextEmail}
                        textContentType = {'emailAddress'}
                        onChangeText = {text => setTextEmail(text)} />
                    <TouchableOpacity
                        onPress={() => {
                            setRetrievePassword(false) ;
                        }}>
                        <Text>Envoyer le nouveau mot de passe</Text>
                    </TouchableOpacity>
            </View>
        </View>
        :false}
        <View
            style={BoxAuthentification.BigMain}>
            <View
                style= {BoxAuthentification.background}>
                <Text>Adresse mail</Text>
                <TextInput
                    autoCompleteType = {'email'}
                    autoCorrect = {false}
                    editable = {true}
                    placeholder = {'Adresse mail'}
                    value = {TextEmail}
                    textContentType = {'emailAddress'}
                    onChangeText = {text => setTextEmail(text)} />
                <Text>Mot de passe</Text>
                <TextInput
                    autoCompleteType = {'password'}
                    autoCorrect = {false}
                    editable = {true}
                    secureTextEntry =  {true}
                    placeholder = {'Mot de passe'}
                    value = {TextPassword}
                    textContentType = {'password'}
                    onChangeText = {text => setTextPassword(text)} />
                <TouchableOpacity
                onPress={() => {
                    setRetrievePassword(true) ;
                }}>
                    <Text>Mot de passe oubli√©</Text>
                </TouchableOpacity>
                <TouchableOpacity
                onPress={() => {
                    login(TextEmail, TextPassword);
                }}>
                    <Text>Se connecter</Text>
                </TouchableOpacity>
                <TouchableOpacity
                onPress={() => {
                    navigation.navigate('Register')
                }}>
                    <Text>Register</Text>
                </TouchableOpacity>
            </View>
        </View>
    </View>
    )
}
